import { DatePipe } from '@angular/common';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';

export class LancamentoFiltro {
  descricao: string = '';
  dataVencimentoInicio?: Date;
  dataVencimentoFim?: Date;
  pagina = 0;
  itensPorPagina = 5;
}

@Injectable({
  providedIn: 'root'
})
export class LancamentoService {

  lancamentosUrl = 'http://localhost:8080/lancamentos';

  constructor(private http: HttpClient, private datePipe: DatePipe) { }

  pesquisar(filtro: LancamentoFiltro): Promise<any> {

    const headers = new HttpHeaders()
      .append('Authorization', 'Bearer eyJraWQiOiI2NTJlNDBjNS02NzJhLTRlMTUtOTMzNi1jMmZlZTEwOGU4MWEiLCJhbGciOiJSUzI1NiJ9..WoR_15hhGYTmgeb0Twk5IOoRkNDinr4y7QaOe2TXniKXf6PxUFHRgOQOoBBCdLS5lP7bEjIVururuEBLiva9dtMYsVDgy4DuN1ldcO6dkhJfBStpz3cuKgjwTMOiumkCOtTj5uMvytN9HvHJvjRX44Tu5vDr-YXqJtyesRVMR2oNCraFTVMPkgLjmVtr-DRyIg6yc1gy3bq1-PmAwWsuJXOrWYwYCDyVZSbjex9YwAcM-8lJA-2-E2KIc0HQxT6P9J3W_jRHorveym9eJHCHuA_J--j9WRc45utlG9hYLegxtfCmeF8OHAcDDmQnRSlvcpqVyoddpNGBYYzUZnOIrg');

    let params = new HttpParams();

    params = params.set('page', filtro.pagina.toString());
    params = params.set('size', filtro.itensPorPagina.toString());

    if (filtro.descricao) {
      params = params.set('descricao', filtro.descricao);
    }

    if (filtro.dataVencimentoInicio) {
      params = params.set('dataVencimentoDe', this.datePipe.transform(filtro.dataVencimentoInicio, 'yyyy-MM-dd')!);
    }

    if (filtro.dataVencimentoFim) {
      params = params.set('dataVencimentoAte', this.datePipe.transform(filtro.dataVencimentoFim, 'yyyy-MM-dd')!);
    }

    return this.http.get(`${this.lancamentosUrl}?resumo`, { headers, params })
      .toPromise()
      .then((response: any) => {
        const lancamentos = response['content'];
        const resultado = {
          lancamentos: lancamentos,
          total: response['totalElements']
        };
        return resultado;
      });
  }
}
